import React ,{useRef,useState} from "react";
import "../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";
import calculateAvgRating from "../utils/avgRating";
import avatar from '../assets/images/avatar.jpg'
import Booking from '../components/Booking/Booking'

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef('')
  const [tourRating, setTourRating]= useState(null)
  // this is an static data later we will call our API and load our data from database
  const tour = tourData.find((tour) => tour.id === id);
  //destructure properties from tour object
  const { photo, title, desc, price, reviews, city, distance, maxGroupSize } =
    tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);
  const options = {day:'numeric', month:'long', year:'numeric'}
  
  
  
  //submit request to the server

  const submitHandler = e =>{
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value
    
  }




  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="6">
              <div className="tour__content">
                <img src={photo} alt="" />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <div className="d-flex align-items-center gap-5">
                    <span className="tour__rating d-flex align-item-center gap-1">
                      <i
                        class="ri-star-fill "
                        style={{ color: "var(--secondary-color)" }}
                      ></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? (
                        "Not Rated"
                      ) : (
                        <span>({reviews?.length})</span>
                      )}
                    </span>
                    <span>
                      <i class="ri-map-pin-user-fill"></i> {city}
                    </span>
                  </div>
                  <div className="tour__extra-details">
                    <span>
                    <i class="ri-wallet-2-fill"></i> Rs.{price} /per
                      person
                    </span>
                    <span>
                    <i class="ri-map-pin-time-line"></i> {distance} k/m</span>
                    <span>
                      
                      <i class="ri-group-line"></i> {maxGroupSize} People
                    </span>
                  </div>
                  <h5>Description</h5>
                  <p>{desc}</p>
                </div>
                {/* =====================tour reviews secction========================== */}
                <div className="tour__reviewws mt-4">
                  <h4>Reviews ({reviews?.length}reviews)</h4>
                  <Form onSubmit = {submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                      <span onClick={()=> setTourRating(1)}>1<i class="ri-star-s-fill"></i></span>
                      <span onClick={()=> setTourRating(2)}>2<i class="ri-star-s-fill"></i></span>
                      <span onClick={()=> setTourRating(3)}>3<i class="ri-star-s-fill"></i></span>
                      <span onClick={()=> setTourRating(4)}>4<i class="ri-star-s-fill"></i></span>
                      <span onClick={()=> setTourRating(5)}>5<i class="ri-star-s-fill"></i></span>

                    </div>
                    <div className="reviews__input">
                      <input type="text" name="" id="" ref={reviewMsgRef} placeholder="share your thoughts" required />
                      <button className="btn primary__btn text-white" type="submit">
                        Submit
                      </button>
                    </div>
                  </Form>
                  <ListGroup className="user__reviews">
                    {
                      reviews?.map(review=>(
                        <div className="review__item">
                          <img src={avatar} alt="" />
                          <div className="w-100">
                            <div className=" d-flex align-items-center justify-content-between">
                              <div>
                                <h5>muhib</h5>
                                <p>
                                  {
                                    new Date("01-02-2025").toLocaleDateString("en-us", options)
                                  }
                                </p>
                              </div>
                              <span className="d-flex align-items-center">
                              5<i class="ri-star-s-fill"></i>
                              </span>
                            </div>
                            <h6>Amazing Tour</h6>
                          </div>
                        </div>
                      ))
                    }

                  </ListGroup>
 
                </div>
                {/* =====================tour reviews secction end========================== */}


              </div>
            </Col>
          <Col lg='6'>
          <Booking tour={tour} avgRating={avgRating}/>
          </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
