import React, { useState } from 'react';
import CommonSecction from './../shared/CommonSection';
import { Container, Row, Col } from 'reactstrap';
import { useLocation } from 'react-router-dom';
import TourCard from './../shared/TourCard';

const SearchResult = () => {
  const location = useLocation();
  const [data] = useState(location.state);  // Get the data passed from the search handler
  console.log(data);  // Log data to check the response

  return (
    <>
      <CommonSecction title="Tour Search Result" />
      <section>
        <Container>
          <Row>
            {data && data.length === 0 ? (
              <h4 className="text-center">No Tour Found</h4>
            ) : (
              data?.map((tour) => (
                <Col lg="3" className="mb-4" key={tour.id}>
                  <TourCard tour={tour} />
                </Col>
              ))
            )}
          </Row>
        </Container>
      </section>
    </>
  );
};

export default SearchResult;
