import React from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./tour-card.css";
import calculateAvgRating from "../utils/avgRating";
import { BASE_URL } from "../utils/config";

const TourCard = ({ tour }) => {
  if (!tour) return null; // ðŸ›¡ Guard clause

  const { _id, title, city, photo, price, featured, reviews = [] } = tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);

  // Combine BASE_URL with photo if it's a relative path
  const photoUrl = photo.startsWith("http") ? photo : `${BASE_URL}${photo}`;

  return (
    <div className="tour__card">
      <Card>
        <div className="tour__img">
          <img src={photoUrl} alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card__top d-flex align-items-center justify-content-between">
            <span className="tour__location d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i> {city}
            </span>
            <span className="tour__rating d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i>
              {avgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                "No Ratings Yet"
              ) : (
                <span>({reviews?.length || 0})</span>
              )}
            </span>
          </div>

          <h5 className="tour__title">
            <Link to={`/tour/${_id}`}>{title}</Link>
          </h5>

          <div className="card__bottom d-flex align-items-center justify-content-between mt-2">
            <h5>
              Rs.{price}
              <span>/per person</span>
            </h5>
            <button className="btn booking__btn">
              <Link to={`/tour/${_id}`}>Book Now</Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;

export const getFeaturedTour = async (req, res) => {
  try {
    const tours = await Tour.find({ featured: true }).limit(8);

    // Add BASE_URL to photo path
    const BASE_URL = "http://localhost:4000";
    const updatedTours = tours.map((tour) => ({
      ...tour._doc,
      photo: `${BASE_URL}${tour.photo}`, // Combine BASE_URL with photo path
    }));

    res.status(200).json({
      success: true,
      message: "Featured tours fetched successfully",
      data: updatedTours,
    });
  } catch (err) {
    res.status(500).json({ success: false, message: "Failed to fetch featured tours", error: err.message });
  }
};
