import React, { useState } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";

const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  const navigate = useNavigate();

  // State to store user input
  const [credentials, setCredentials] = useState({
    userId: "01",
    userEmail: "example@gmail.com",
    fullName: "",
    phone: "",
    guestSize: 1,
    bookAt: "",
  });

  // Function to handle input changes
  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  // Fixed service charge
  const serviceFee = 830;
  const totalAmount =
    Number(price) * Number(credentials.guestSize) + Number(serviceFee);

  // Function to handle form submission with validation
  const handleClick = (e) => {
    e.preventDefault();

    // Validation for empty fields
    if (
      !credentials.fullName ||
      !credentials.phone ||
      !credentials.bookAt ||
      !credentials.guestSize
    ) {
      alert("Please fill all required fields!");
      return;
    }

    // Phone number validation (must be 10 digits)
    if (!/^\d{10}$/.test(credentials.phone)) {
      alert("Please enter a valid 10-digit phone number!");
      return;
    }

    // Guest size validation (minimum 1 guest)
    if (credentials.guestSize < 1) {
      alert("Guest size must be at least 1!");
      return;
    }

    // Navigate to thank-you page if all validations pass
    navigate("/Thank-you");
  };

  return (
    <div className="booking">
      {/* Top Section: Price and Rating */}
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          Rs.{price}
          <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i className="ri-star-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>

      {/* Booking Form */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Full Name"
              id="fullName"
              onChange={handleChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              placeholder="Phone"
              id="phone"
              onChange={handleChange}
              required
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            <input type="date" id="bookAt" onChange={handleChange} required />
            <input
              type="number"
              placeholder="Guest"
              id="guestSize"
              min="1"
              onChange={handleChange}
              required
            />
          </FormGroup>
        </Form>
      </div>

      {/* Booking Summary */}
      <div className="booking__botton">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              Rs.{price} <i className="ri-close-line"></i>Per person
            </h5>
            <span>Rs.{price}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service charge</h5>
            <span>Rs.{serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>Rs.{totalAmount}</span>
          </ListGroupItem>
        </ListGroup>

        {/* Booking Button */}
        <Button
          className="btn primary__btn w-100 mt-4"
          onClick={handleClick}
          type="submit"
        >
          Book Now
        </Button>
      </div>
    </div>
  );
};

export default Booking;
