import React, { useRef } from "react";
import "./search-bar.css";
import { Col, Form, FormGroup } from "reactstrap";

const SearchBar = ({ onSearch }) => {
  const locationRef = useRef(null);
  const distanceRef = useRef(null);
  const maxGroupSizeRef = useRef(null);

  const searchHandler = (event) => {
    event.preventDefault(); // Prevent form submission behavior

    const location = locationRef.current.value.trim();
    const distance = distanceRef.current.value.trim();
    const maxGroupSize = maxGroupSizeRef.current.value.trim();

    if (!location || !distance || !maxGroupSize) {
      alert("All fields are required!");
      return;
    }

    // Pass search data to parent component or API
    const searchData = {
      location,
      distance: parseInt(distance, 10),
      maxGroupSize: parseInt(maxGroupSize, 10),
    };

    console.log("Searching with:", searchData);
    
    if (onSearch) {
      onSearch(searchData); // Pass data to parent component
    }
  };

  return (
    <Col lg="12">
      <div className="search__bar">
        <Form className="d-flex align-items-center gap-4" onSubmit={searchHandler}>
          <FormGroup className="d-flex gap-3 form__group form__group-fast">
            <span><i className="ri-map-pin-line"></i></span>
            <div>
              <h6>Location</h6>
              <input type="text" placeholder="Where are you going?" ref={locationRef} />
            </div>
          </FormGroup>

          <FormGroup className="d-flex gap-3 form__group form__group-fast">
            <span><i className="ri-map-pin-time-line"></i></span>
            <div>
              <h6>Distance</h6>
              <input type="number" placeholder="Distance (km)" ref={distanceRef} />
            </div>
          </FormGroup>

          <FormGroup className="d-flex gap-1 form__group">
            <span><i className="ri-group-line"></i></span>
            <div>
              <h6>Max People</h6>
              <input type="number" placeholder="0" ref={maxGroupSizeRef} />
            </div>
          </FormGroup>

          <button className="search__icon" type="submit">
            <i className="ri-search-line"></i>
          </button>
        </Form>
      </div>
    </Col>
  );
};

export default SearchBar;
