import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/about.css"; // Ensure this CSS file exists

const About = () => {
  const navigate = useNavigate();

  return (
    <div className="about-container">
      {/* Header Section */}
      <h2 className="about-title">About Us</h2>

      {/* Main Content */}
      <div className="about-content">
        {/* Left Side - Image */}
        <div className="about-image">
          <img
            src="https://source.unsplash.com/600x400/?adventure,travel"
            alt="Travel Adventure"
          />
        </div>

        {/* Right Side - Text */}
        <div className="about-text">
          <p>
            Welcome to <strong>YatraVenture</strong>, where adventure meets
            convenience! üåç We are passionate about crafting unforgettable
            travel experiences for wanderlust seekers.
          </p>
          <h4>Why Choose Us?</h4>
          <ul>
            <li>‚úÖ Expert Travel Guides</li>
            <li>‚úÖ 100+ Amazing Destinations</li>
            <li>‚úÖ Best Price Guarantee</li>
            <li>‚úÖ 24/7 Customer Support</li>
          </ul>
        </div>
      </div>

      {/* Call to Action Section */}
      <div className="about-cta">
        <h3>Ready to Explore the World? üåé</h3>
        <p>Join us on an unforgettable journey today!</p>
        <button className="cta-button" onClick={() => navigate("/register")}>
          Get Started
        </button>
      </div>
    </div>
  );
};

export default About;
