import React, { useEffect, useState } from "react";
import TourCard from "../../shared/TourCard";
import { Col } from "reactstrap";
import { BASE_URL } from "../../utils/config";
import useFetch from "../../hooks/useFatch";

const FeaturedTourList = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const {data: featuredTours}=useFetch( `${BASE_URL}/tours/search/getFeaturedTour`); // Corrected endpoint

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        console.log("Fetching data from:", url); // Debug log
        const res = await fetch(url);
        if (!res.ok) {
          throw new Error("Failed to fetch data");
        }
        const result = await res.json();
        console.log("API Response:", result); // Debug log
        setData(result.data || []); // Handle empty response
      } catch (err) {
        console.error("Fetch Error:", err.message); // Debug log
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [url]);
  console.log("Data:", data); // Debug log

  if (loading) return <h4>Loading...</h4>;
  if (error) return <h4>{error}</h4>;
  if (!data || data.length === 0) return <h4>No featured tours found</h4>;

  return (
    <>
      {data.map((tour) => (
        <Col lg="3" className="mb-4" key={tour._id}>
          <TourCard tour={tour} />
        </Col>
      ))}
    </>
  );
};

export default FeaturedTourList;
