import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-extra-markers/dist/css/leaflet.extra-markers.min.css";
import "leaflet-extra-markers/dist/js/leaflet.extra-markers";
import tours from "../tours.json"; // Importing tours.json file

// Custom icons for different places
const customIcon = L.ExtraMarkers.icon({
  icon: "fa-map-marker-alt",
  markerColor: "blue",
  shape: "circle",
  prefix: "fa",
});

const UttarakhandTour = () => {
  return (
    <div
      style={{
        height: "500px",
        width: "100%",
        marginTop: "20px",
        borderRadius: "10px",
        overflow: "hidden",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
      }}
    >
      <MapContainer
        center={[30.0668, 79.0193]}
        zoom={5}
        style={{ height: "100%", width: "100%" }}
        attributionControl={false}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {tours.map((tour, index) => (
          <Marker key={index} position={[tour.lat, tour.lng]} icon={customIcon}>
            <Popup>
              <div style={{ textAlign: "center" }}>
                <img
                  src={tour.photo}
                  alt={tour.title}
                  style={{ width: "120px", height: "80px", borderRadius: "5px" }}
                  onError={(e) => (e.target.src = "https://via.placeholder.com/120x80?text=No+Image")}
                />
                <h4>{tour.title}</h4>
                <p style={{ fontSize: "14px", color: "#666" }}>{tour.desc}</p>
                <p><strong>City:</strong> {tour.city}</p>
                <p><strong>Price:</strong> ${tour.price}</p>
                <p><strong>Rating:</strong> {tour.reviews.length > 0 ? tour.reviews.reduce((a, b) => a + b, 0) / tour.reviews.length : "Not Rated"}</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default UttarakhandTour;
