const calculateAvgRating = reviews =>{
    const totalRating = reviews((acc, item) => acc + item.rating, 0);
  const avgRating =
    totalRating === 0
      ? ''
      : totalRating === 1
      ? totalRating
      : totalRating / reviews?.length;
      return {
        totalRating,
        avgRating
    
      }
}
export default calculateAvgRating
