import "../styles/about.css"; // CSS file include karna mat bhoolna

const About = () => {
  return (
    <div className="about-container">
      {/* Header Section */}
      <h2 className="about-title">About Us</h2>

      {/* Main Content */}
      <div className="about-content">
        {/* Left Side - Image */}
        <div className="about-image">
          <img
            src="https://source.unsplash.com/600x400/?adventure,travel"
            alt="About Us"
          />
        </div>

        {/* Right Side - Text */}
        <div className="about-text">
          <h3>Explore the World with <span className="highlight">YatraVenture</span></h3>
          <p>
            <strong>YatraVenture</strong> is your trusted travel companion, offering unique and unforgettable travel experiences. Whether you're looking for adventure, relaxation, or cultural exploration, we have something for everyone.
          </p>
          
          <h4>Why Choose Us?</h4>
          <ul>
            <li>üåç 100+ Handpicked Destinations</li>
            <li>üó∫Ô∏è Expert Travel Guides</li>
            <li>üí∞ Affordable & Transparent Pricing</li>
            <li>üìû 24/7 Customer Support</li>
          </ul>
        </div>
      </div>

      {/* Call to Action Section */}
      <div className="about-cta">
        <h3>Start Your Adventure Today! üåé</h3>
        <p>Join thousands of happy travelers and make unforgettable memories.</p>
        <button className="cta-button">Get Started</button>
      </div>
    </div>
  );
};

export default About;
