import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-extra-markers/dist/css/leaflet.extra-markers.min.css";
import "leaflet-extra-markers/dist/js/leaflet.extra-markers";

// Custom icons for different places
const icons = {
  mountain: L.ExtraMarkers.icon({
    icon: "fa-mountain",
    markerColor: "blue",
    shape: "circle",
    prefix: "fa",
  }),
  temple: L.ExtraMarkers.icon({
    icon: "fa-place-of-worship",
    markerColor: "red",
    shape: "square",
    prefix: "fa",
  }),
  river: L.ExtraMarkers.icon({
    icon: "fa-water",
    markerColor: "green",
    shape: "star",
    prefix: "fa",
  }),
};

const UttarakhandTour = () => {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    fetch("/tours.json")
      .then((response) => response.json())
      .then((data) => setPlaces(data))
      .catch((error) => console.error("Error loading tour data:", error));
  }, []);

  return (
    <div style={{ height: "500px", width: "100%", marginTop: "20px", borderRadius: "10px", overflow: "hidden", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)" }}>
      <MapContainer center={[30.0668, 79.0193]} zoom={8} style={{ height: "100%", width: "100%" }} attributionControl={false} zoomControl={true}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {places.map((place, index) => (
          <Marker key={index} position={[place.latitude, place.longitude]} icon={icons[place.type] || icons.mountain}>
            <Popup>
              <div style={{ textAlign: "center" }}>
                <img src={place.photo} alt={place.title} style={{ width: "120px", height: "80px", borderRadius: "5px" }} onError={(e) => e.target.src = "https://via.placeholder.com/120x80?text=No+Image"} />
                <h4>{place.title}</h4>
                <p style={{ fontSize: "14px", color: "#666" }}>{place.desc}</p>
                <p style={{ fontSize: "14px", color: "#333" }}>Rating: {place.rating ? place.rating : "Not Rated"}</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default UttarakhandTour;
